import { StytchUIClient } from './StytchUIClient';
import { StytchLoginConfig, StyleConfig, Callbacks, StytchB2BUIConfig } from '@stytch/core/public';
import { StytchB2BUIClient } from './b2b/StytchB2BUIClient';
import { StytchProjectConfigurationInput } from '@stytch/core/public';
import { IDPConsentScreenManifest } from '@stytch/core/public';
export type SDKConfig<TProjectConfiguration extends StytchProjectConfigurationInput> = {
    client: StytchUIClient<TProjectConfiguration>;
    config: StytchLoginConfig;
    styles?: StyleConfig;
    callbacks?: Callbacks<TProjectConfiguration>;
};
export type B2BSDKConfig<TProjectConfiguration extends StytchProjectConfigurationInput> = {
    client: StytchB2BUIClient<TProjectConfiguration>;
    config: StytchB2BUIConfig;
    styles?: StyleConfig;
    callbacks?: Callbacks<TProjectConfiguration>;
};
export type ResetPasswordSDKConfig<TProjectConfiguration extends StytchProjectConfigurationInput> = SDKConfig<TProjectConfiguration> & {
    passwordResetToken: string;
};
export type IDPSDKConfig<TProjectConfiguration extends StytchProjectConfigurationInput> = Pick<SDKConfig<TProjectConfiguration>, 'client' | 'styles' | 'callbacks'>;
export type B2BIDPConsentManifestGenerator = (input: {
    scopes: string[];
    clientName: string;
}) => IDPConsentScreenManifest;
export type B2BIDPSDKConfig<TProjectConfiguration extends StytchProjectConfigurationInput> = Pick<B2BSDKConfig<TProjectConfiguration>, 'client' | 'styles' | 'callbacks'> & {
    getIDPConsentManifest?: B2BIDPConsentManifestGenerator;
};
